import { parse } from 'querystring'
import moment from 'moment'
import { JSEncrypt } from 'jsencrypt'
/* eslint no-useless-escape:0 import/prefer-default-export:0 */

const reg = /(((^https?:(?:\/\/)?)(?:[-;:&=\+\$,\w]+@)?[A-Za-z0-9.-]+(?::\d+)?|(?:www.|[-;:&=\+\$,\w]+@)[A-Za-z0-9.-]+)((?:\/[\+~%\/.\w-_]*)?\??(?:[-\+=&;%@.\w_]*)#?(?:[\w]*))?)$/
export const isUrl = (path) => reg.test(path)
export const isAntDesignPro = () => {
  if (ANT_DESIGN_PRO_ONLY_DO_NOT_USE_IN_YOUR_PRODUCTION === 'site') {
    return true
  }

  return window.location.hostname === 'preview.pro.ant.design'
} // 给官方演示站点用，用于关闭真实开发环境不需要使用的特性

export const isAntDesignProOrDev = () => {
  const { NODE_ENV } = process.env

  if (NODE_ENV === 'development') {
    return true
  }

  return isAntDesignPro()
}
export const getPageQuery = () => parse(window.location.href.split('?')[1])

export const addGetQuery = (params, oldQuery) => {
  let query = oldQuery
  if (params) {
    if (params.current_page) {
      query += `&current_page=${params.current_page}`
    }
    if (params.per_page) {
      query += `&per_page=${params.per_page}`
    }
    if (params.sorted_by) {
      query += `&sorted_by=${params.sorted_by}`
    }
    if (params.sorted_by_type) {
      query += `&sorted_by_type=${params.sorted_by_type}`
    }
  }
  return query
}

// 处理params中有none的情况
export const formatParams = (params) => {
  Object.keys(params).map(key => (params[key] === null ? delete params[key] : ''))
}

export const formatDatetime = (datetimeStr, type = 'month') => {
  if (type === 'year') {
    return moment(datetimeStr, 'YYYYMMDD HH:mm:ss').utcOffset(+960).format('YYYY-MM-DD HH:mm')
  }
  return moment(datetimeStr, 'YYYYMMDD HH:mm:ss').utcOffset(+960).format('MM-DD HH:mm')
}

export const dateFormat = 'YYYY-MM-DD'

export const formatDate = (dateStr) => {
  // 不需要管时区问题，只是选择日期，等于0点，不会差出1天
  return moment(dateStr, dateFormat)
}

export const generate_sn_str = (number_in, number_len) => {
  let template = Math.pow(10, (number_len))
  return (number_in + template).toString().substring(1)
}

